<?php/*Plugin Name:BuddyPress Profile ShortcodesDescription:This will provide shortcodes to access data in a user's buddypress profile.Version: 1.1Author: Bento4ExtendAuthor URI: http://extend.bt4.me/Plugin URI: http://extend.bt4.me/downloads/buddypress-profile-shortcodes/*/function bpsc_plugin_init() {    require( dirname( __FILE__ ) . '/Bpsc_shortcodes.php');    $obj = new Bpcs_shortcodes();}add_action( 'bp_include', 'bpsc_plugin_init' );function bpsc_check_buddypress(){    if(!class_exists('BuddyPress')):        add_action( 'admin_notices', 'bp_notExist_admin_notice' );    endif;}add_action( 'plugins_loaded', 'bpsc_check_buddypress' );function bp_notExist_admin_notice() {    ?>    <div class="updated">        <p><?php _e( 'BuddyPress have to be installed for BuddyPress Profile Shortcodes Plugin to work.', 'Bpsc' ); ?></p>    </div><?php}add_filter( 'plugin_row_meta', 'bpsc_custom_plugin_row_meta', 10, 2 );function bpsc_custom_plugin_row_meta( $links, $file ) {	if ( strpos( $file, 'buddypress_profile_shortcodes.php' ) !== false ) {		$new_links = array(					'<a href="http://extend.bt4.me/downloads/buddypress-profile-shortcodes/" target="_blank">BuddyPress Profile Shortcodes</a>',					'<a href="http://extend.bt4.me/downloads/buddypress-profile-custom-area/" target="_blank">BuddyPress Profile Custom Area</a>',					'<a href="http://extend.bt4.me/downloads/buddypress-profile-geolocation/" target="_blank">BuddyPress Profile GeoLocation</a>'				);				$links = array_merge( $links, $new_links );	}	return $links;}